import pandas as pd
import numpy as np
import matplotlib.pyplot as plt
import seaborn as sns

data=pd.read_csv("vgsales.csv")

data

data.describe()

data["Name"].value_counts()

data[data["Name"]=="Ice Hockey"]

# @title xlim and ylim
# use line graph to see the trend
ih=data[data["Name"]=="Ice Hockey"]
sns.lineplot(data=ih,x="Year",y="NA_Sales",c="red")
plt.title("Ice Hockey Sales Trend")
plt.xlim(1995,2010)
plt.ylim(0.3,1.0)
plt.show()

baseball=data[data["Name"]=="Baseball"]
sns.lineplot(data=baseball,x="Year",y="NA_Sales",c="green")
plt.title("Base Ball Sales Trend")
plt.show()

# @title To plot two line plots together
sns.lineplot(data=ih,x="Year",y="NA_Sales",c="red")
sns.lineplot(data=baseball,x="Year",y="NA_Sales",c="green")
plt.show()

sns.lineplot(data=ih,x="Year",y="NA_Sales")
sns.lineplot(data=baseball,x="Year",y="NA_Sales")
plt.show()

#@title label lines
sns.lineplot(data=ih,x="Year",y="NA_Sales",label="Ice Hockey")
sns.lineplot(data=baseball,x="Year",y="NA_Sales",label="Baseball")
plt.legend(loc="lower center")
plt.show()

#@title legend location
sns.lineplot(data=ih,x="Year",y="NA_Sales",label="Ice Hockey")
sns.lineplot(data=baseball,x="Year",y="NA_Sales",label="Baseball")
plt.legend(loc="upper left")
plt.show()

#@title legend location
sns.lineplot(data=ih,x="Year",y="NA_Sales",label="Ice Hockey")
sns.lineplot(data=baseball,x="Year",y="NA_Sales",label="Baseball")
plt.legend(loc="best",frameon=False)
plt.show()

# @title Scatter Plot
# Used if you want to find relationship between two numerical parameters
# Correlation b/w 2 variables

"""![image.png](data:image/png;base64,)"""

data.head()

sns.lineplot(data=data, x="Global_Sales",y="Rank")
plt.show()

sns.scatterplot(data=data, x="Global_Sales",y="Rank")
plt.show()

sns.scatterplot(data=data, x="NA_Sales",y="Global_Sales")
plt.show()

sns.regplot(data=data, x="NA_Sales",y="Global_Sales")
plt.show()

# Category- Category
# Publisher | Genres | Platforms

# top 3 publisher
top3_pub=data['Publisher'].value_counts().index[:3]

top3_gen=data["Genre"].value_counts().index[:3]

top3_plat=data["Platform"].value_counts().index[:3]

top3_data = data.loc[(data["Publisher"].isin(top3_pub))&(data["Genre"].isin(top3_gen))&(data["Platform"].isin(top3_plat))]

top3_data

sns.countplot(data=top3_data, x="Publisher")
plt.show()

# Countplot-
#           1. Dodge Countplot
#           2. Stacked Countplot

# Dodge countplot
sns.countplot(data=top3_data,x="Publisher",hue="Genre")
plt.show()

# Stack Count Plot
sns.countplot(data=top3_data,x="Publisher",hue="Genre",dodge=False)
plt.show()

# Numerical- Categorical
# Global Slaes Vs Publisher

sns.boxplot(data=data,y="Global_Sales")
plt.show()

sns.boxplot(data=top3_data,y="Global_Sales",x="Publisher")
plt.title("Sales for top 3 publisher")
plt.show()

sns.boxplot(data=top3_data,y="Global_Sales",x="Genre")
plt.title("Sales for top 3 publisher")
plt.show()

sns.barplot(data=top3_data,y="Global_Sales",x="Publisher")
plt.show()

data[data["Publisher"]=="Electronic Arts"]

# @title SubPlots

plt.figure(figsize=(15,10))
plt.suptitle("Comparision of NA Sales vs. other ")
plt.subplot(2,3,1)
sns.scatterplot(data=top3_data, x="NA_Sales",y="Global_Sales",c="g")
plt.subplot(2,3,3)
sns.scatterplot(data=top3_data, x="NA_Sales",y="EU_Sales",c="orange")
plt.subplot(2,3,4)
sns.scatterplot(data=top3_data, x="NA_Sales",y="JP_Sales")
plt.subplot(2,3,6)
sns.scatterplot(data=top3_data, x="NA_Sales",y="Other_Sales")
plt.subplot(1,3,2)
sns.boxplot(data=top3_data,x="Publisher", y="NA_Sales")
plt.show()

