#for each year, how you will get the average, lowest and highest value of ratings of movies?
data.groupby('year').agg({"vote_average":['min',"mean",'max']})

data.groupby('year').agg({"vote_average":['min',"mean",'max']}).tail()

##Ques- find the details of the movies by high budget directors.
# "High budget directors" --> atleast 1 movie with budget>100M dollars
data.head()

data.groupby("director_name").agg({"budget":"max"}).sort_values("budget",ascending=False)

data_dir_budget=data.groupby("director_name")["budget"].max().reset_index()
data_dir_budget.head()

names=data_dir_budget[data_dir_budget['budget']>100000000]["director_name"]

names

data[data['director_name'].isin(names)]

# filter

def isHighBudget(x):
  return x["budget"].max()>= 100000000

data.groupby("director_name").filter(isHighBudget)

#filter_function can also be applied in DataFrame

# Risky movie?
#--budget of the movie > the avg budget of the director
#step1 : avg budget of the director
#step2 : budget of the movie> avg budget of director
#step3 : create a new column data['risky]

def risky(x):
  x['risky']= x["budget"]-x["budget"].mean() >= 0 # x["budget"].mean() -- Average budget
  return x
data.groupby('director_name').apply(risky)

data[data["director_name"]=="Christopher Nolan"]['budget'].mean()

